function Movie({ cover, id, title, rating, genres }) {
    return (
        <div key={id}>
            <h2>{title} [{rating}]</h2>
            {cover ? <img src={cover} ></img> : null}
            <ul>
                {genres ? genres.map((g, index) => (<li key={index}>{g}</li>)) : null}
            </ul>
            <hr></hr>
        </div>)
}
