import logo from './logo.svg';
import './App.css';
import Movie from './Movie.js';
import { useEffect, useState } from 'react';

function App() {
  const [loading, setLoading] = useState(true);
  const [movies, setMovies] = useState([]);

  //api로 정보를 불러온 후 json으로 변경, json 파일을 state로 사용한다
  //arrow function, async, await을 사용, 코드 줄여서 쓰는 경우, 왜 사용할까?
  const getMovies = async () => {
    const json = await (
      await fetch('https://yts.mx/api/v2/list_movies.json?minimum_rating=8.5&sort_by=year'))
      .json();
    setMovies(json.data.movies);
    setLoading(false);
  };

  /* arrow function, async, await을 사용
  const getMovies = async () => {
    const response = await fetch('https://yts.mx/api/v2/list_movies.json?minimum_rating=8.5&sort_by=year');
    const json = await response.json();
    setMovies(json.data.movies);
    setLoading(false);
  };
  */

  /* then 사용
  const getMovies = () => {
    fetch('https://yts.mx/api/v2/list_movies.json?minimum_rating=8.0&sort_by=year')
    .then((response) => response.json())
    .then((json) => {
      setMovies(json.data.movies);
      setLoading(false);
    });
  };
  */


  useEffect(() => {
    getMovies();
  }, []);

  //console.log(movies);

  return (
    <div className="App">
      {loading ? <h1>loading</h1> :
        /*
        <div>
          {movies.map((item) =>
            <div key={item.id}>
              <h2>{item.title} [{item.rating}]</h2>
              {item.medium_cover_image ? <img src={item.medium_cover_image} ></img> : null}
              <ul>
                {item.genres ? item.genres.map((g, index) => (<li key={index}>{g}</li>)) : null}
              </ul>
              {item.url}
              <p>{item.summary}</p>
              <hr></hr>
            </div>)}
        </div>
        */
        <div>
          {movies.map((item) => <Movie
            cover={item.medium_cover_image}
            id={item.id}
            title={item.title}
            rating={item.rating}
            genres={item.genres} />
          )}
        </div>


        //<Movie />
      }
    </div >
  );
}

export default App;
